package com.jiagouedu.services.manage.product;import com.jiagouedu.core.Services;import com.jiagouedu.services.manage.product.bean.ProductStockInfo;import com.jiagouedu.services.manage.product.bean.FrontProduct;import java.util.List;public interface FrontProductService extends Services<FrontProduct> {	/**	 * 商品上架	 * @param ids	 */	void upGoods(String[] ids);		/**	 * 商品下架	 * @param ids	 */	void downGoods(String[] ids);	/**	 * 根据商品关键字搜索商品列表	 * @return	 */	List<FrontProduct> search(FrontProduct e);	/**	 * 查询商品参数列表	 * @param id	 * @return	 */	List<FrontProduct> selectParameterList(String id);	/**	 * 加载商品库存列表	 * @param product	 * @return	 */	List<ProductStockInfo> selectStockList(FrontProduct product);		/**	 * 加载商品介绍信息	 * @param product	 * @return	 */	List<FrontProduct> selectListProductHTML(FrontProduct product);		/**	 * 根据商品ID集合加载商品信息	 * @param p	 * @return	 */	List<FrontProduct> selectProductListByIds(FrontProduct p);	/**	 * 查询热门搜索的商品	 * @return	 */	List<FrontProduct> selectHotSearch(FrontProduct p);	/**	 * 加载热卖商品列表，此数据将会在门户的超级菜单上显示出来。 	 * @param product	 * @return	 */	List<FrontProduct> loadHotProductShowInSuperMenu(FrontProduct product);	/**	 * 更新商品的浏览次数	 * @param p	 */	void updateHit(FrontProduct p);	/**	 * 加载页面左侧商品列表	 * @param p	 * @return	 */	List<FrontProduct> selectPageLeftHotProducts(FrontProduct p);	/**	 * 加载促销活动的商品	 * @return	 */	List<FrontProduct> selectActivityProductList(FrontProduct p);}