package com.jiagouedu.services.manage.product.impl;import com.jiagouedu.core.ServersManager;import com.jiagouedu.services.manage.product.FrontProductService;import com.jiagouedu.services.manage.product.bean.FrontProduct;import com.jiagouedu.services.manage.product.bean.ProductStockInfo;import com.jiagouedu.services.manage.product.dao.FrontProductDao;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;@Servicepublic class FrontProductServiceImpl extends ServersManager<FrontProduct, FrontProductDao> implements		FrontProductService {    @Override    @Resource(name = "productDaoFront")    public void setDao(FrontProductDao productDao) {        this.dao = productDao;    }	@Override	public void upGoods(String[] ids) {		upOrDown(ids, 1);	}	@Override	public void downGoods(String[] ids) {		upOrDown(ids, 2);	}	/**	 * @param ids	 */	private void upOrDown(String[] ids, int status) {		if (ids == null || ids.length == 0) {			return;		}		for (int i = 0; i < ids.length; i++) {			FrontProduct goods = new FrontProduct();			goods.setId(ids[i]);			goods.setStatus(status);			dao.update(goods);		}	}	@Override	public List<FrontProduct> search(FrontProduct e) {		return dao.selectList(e);	}	@Override	public List<FrontProduct> selectParameterList(String id) {		return dao.selectParameterList(id);	}	@Override	public List<ProductStockInfo> selectStockList(FrontProduct product) {		return dao.selectStockList(product);	}	@Override	public List<FrontProduct> selectListProductHTML(FrontProduct product) {		return dao.selectListProductHTML(product);	}	@Override	public List<FrontProduct> selectProductListByIds(FrontProduct p) {		return dao.selectProductListByIds(p);	}	@Override	public List<FrontProduct> selectHotSearch(FrontProduct p) {		return dao.selectHotSearch(p);	}	@Override	public List<FrontProduct> loadHotProductShowInSuperMenu(FrontProduct product) {		return dao.loadHotProductShowInSuperMenu(product);	}	@Override	public void updateHit(FrontProduct p) {        dao.updateHit(p);	}	@Override	public List<FrontProduct> selectPageLeftHotProducts(FrontProduct p) {		return dao.selectPageLeftHotProducts(p);	}	@Override	public List<FrontProduct> selectActivityProductList(FrontProduct p) {		return dao.selectActivityProductList(p);	}}